#include "test.h"

static void donothing() {}

Test::Test() {
    _mycb = donothing;
    _number = 0;
}

void Test::something() {
    //...
    _number = 123; // how to pass this to my callback
    _mycb.call();
}

int Test::get(){
    return _number;
}

void Test::attach(Callback<void()> function) {
    if (function) {
        _mycb = function;

    } else {
        _mycb = donothing;
    }
}
```

`main.cpp`
```cpp
#include <mbed.h>
#include "test.h"

Test test;

void callback() {
    printf("get number here somehow\n");

    // or is this the only way
    printf("number: %i\n", test.get());
}

int main() {
    test.attach(callback);
    test.something();

    while (true);
}
