                tr_debug("\r\n%" PRIu32 "/%" PRIu32 " writing %" PRIu32 " bytes to 0x%08" PRIX32,
                         offset, (uint32_t) details->size, programSize, app_start_addr + offset);
                printf("\r\nXXX - %" PRIu32 "/%" PRIu32 " writing %" PRIu32 " bytes to 0x%08" PRIX32 "\n",
                         offset, (uint32_t) details->size, programSize, app_start_addr + offset);

                offset += programSize;
