int main(){
//after mesh.connect success
uint8_t buf[10] = "abcd";
sock_id = socket_open(SOCKET_UDP, 8080, socket_callback);
status = socket_sendto(sock_id, &address, (uint8_t *)buf, sizeof(buf));
